const initState = {
    search: '',
    sort: {
        sortBy: '',
        sortDir: ''
    },
    toggle: {
        status: false,
        action: ''
    }
}

const filters = (state = initState, action) => {
    switch (action.type) {
        case "CONTROL/TOGGLE":
            return {...state, toggle: {
                status: !state.status,
                action: action.payload.action
            } }
        default:
            return state
    }
}

export default filters
